Apperyio.AppPages = [{
    "name": "startScreen",
    "location": "startScreen.html"
}, {
    "name": "MapView",
    "location": "MapView.html"
}, {
    "name": "Dashboard",
    "location": "Dashboard.html"
}];

Dashboard_js = function(runBeforeShow) {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobilebutton_2': 'Dashboard_mobilebutton_2',
        'spacer_15': 'Dashboard_spacer_15',
        'mobilelabel_8': 'Dashboard_mobilelabel_8',
        'mobiletoggle_7': 'Dashboard_mobiletoggle_7',
        'spacer_14': 'Dashboard_spacer_14',
        'mobilebutton_13': 'Dashboard_mobilebutton_13'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    if (navigator.userAgent.indexOf("IEMobile") != -1) {
        //Fix for jQuery Mobile's footer in Cordova webview on WP8 devices
        var msViewportStyle = document.createElement("style");
        msViewportStyle.appendChild(
        document.createTextNode("@media screen and (orientation: portrait){@-ms-viewport {width: 320px; height: 534px;user-zoom: fixed;zoom: 1;}}" + "@media screen and (orientation:landscape){@-ms-viewport{width:480px;user-zoom:fixed;zoom:1;}}"));
        document.getElementsByTagName("head")[0].appendChild(msViewportStyle);
    }

    Apperyio.CurrentScreen = 'Dashboard';

    /*
     * Nonvisual components
     */
    var datasources = [];

    /*
     * Events and handlers
     */

    // Before Show
    var Dashboard_beforeshow = function() {
            Apperyio.CurrentScreen = "Dashboard";
            for (var idx = 0; idx < datasources.length; idx++) {
                datasources[idx].__setupDisplay();
            }
        };

    // On Load
    var Dashboard_onLoad = function() {
            Dashboard_elementsExtraJS();

            // TODO fire device events only if necessary (with JS logic)
            Dashboard_deviceEvents();
            Dashboard_windowEvents();
            Dashboard_elementsEvents();
        };

    // screen window events
    var Dashboard_windowEvents = function() {

            $('#Dashboard').bind('pageshow orientationchange', function() {
                var _page = this;
                adjustContentHeightWithPadding(_page);
            });

        };

    // device events
    var Dashboard_deviceEvents = function() {
            document.addEventListener("deviceready", function() {

            });
        };

    // screen elements extra js
    var Dashboard_elementsExtraJS = function() {
            // screen (Dashboard) extra code

            /* mobiletoggle_7 */

            $("#Dashboard_mobiletoggle_7").parent().find(".ui-flipswitch-on").attr("tabindex", "3");

        };

    // screen elements handler
    var Dashboard_elementsEvents = function() {
            $(document).on("click", "a :input,a a,a fieldset label", function(event) {
                event.stopPropagation();
            });

            $(document).off("click", '#Dashboard_mobileheader [name="mobilebutton_2"]').on({
                click: function() {
                    if (!$(this).attr('disabled')) {
                        Apperyio.navigateTo('startScreen', {
                            transition: 'flip',
                            reverse: false
                        });

                    }
                },
            }, '#Dashboard_mobileheader [name="mobilebutton_2"]');

        };

    $(document).off("pagebeforeshow", "#Dashboard").on("pagebeforeshow", "#Dashboard", function(event, ui) {
        Dashboard_beforeshow();
    });

    if (runBeforeShow) {
        Dashboard_beforeshow();
    } else {
        Dashboard_onLoad();
    }
};

$(document).off("pagecreate", "#Dashboard").on("pagecreate", "#Dashboard", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    Dashboard_js();
});