startScreen_js = function(runBeforeShow) {

    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobileimage_62': 'startScreen_mobileimage_62',
        'mobilebutton_89': 'startScreen_mobilebutton_89',
        'mobilebutton_88': 'startScreen_mobilebutton_88',
        'mobilebutton_86': 'startScreen_mobilebutton_86',
        'mobiletoggle_87': 'startScreen_mobiletoggle_87',
        'mobilenavbar_6': 'startScreen_mobilenavbar_6',
        'mobilenavbaritem_7': 'startScreen_mobilenavbaritem_7',
        'mobilenavbaritem_8': 'startScreen_mobilenavbaritem_8',
        'mobilenavbaritem_9': 'startScreen_mobilenavbaritem_9'
    };

    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }

    if (navigator.userAgent.indexOf("IEMobile") != -1) {
        //Fix for jQuery Mobile's footer in Cordova webview on WP8 devices
        var msViewportStyle = document.createElement("style");
        msViewportStyle.appendChild(
        document.createTextNode("@media screen and (orientation: portrait){@-ms-viewport {width: 320px; height: 534px;user-zoom: fixed;zoom: 1;}}" + "@media screen and (orientation:landscape){@-ms-viewport{width:480px;user-zoom:fixed;zoom:1;}}"));
        document.getElementsByTagName("head")[0].appendChild(msViewportStyle);
    }

    Apperyio.CurrentScreen = 'startScreen';

    /*
     * Nonvisual components
     */
    var datasources = [];

    /*
     * Events and handlers
     */

    // Before Show
    var startScreen_beforeshow = function() {
            Apperyio.CurrentScreen = "startScreen";
            for (var idx = 0; idx < datasources.length; idx++) {
                datasources[idx].__setupDisplay();
            }
        };

    // On Load
    var startScreen_onLoad = function() {
            startScreen_elementsExtraJS();

            // TODO fire device events only if necessary (with JS logic)
            startScreen_deviceEvents();
            startScreen_windowEvents();
            startScreen_elementsEvents();
        };

    // screen window events
    var startScreen_windowEvents = function() {

            $('#startScreen').bind('pageshow orientationchange', function() {
                var _page = this;
                adjustContentHeightWithPadding(_page);
            });

        };

    // device events
    var startScreen_deviceEvents = function() {
            document.addEventListener("deviceready", function() {

            });
        };

    // screen elements extra js
    var startScreen_elementsExtraJS = function() {
            // screen (startScreen) extra code

            /* mobiletoggle_87 */

            $("#startScreen_mobiletoggle_87").parent().find(".ui-flipswitch-on").attr("tabindex", "12");
            $("#startScreen_mobiletoggle_87").val("on").flipswitch('refresh');

        };

    // screen elements handler
    var startScreen_elementsEvents = function() {
            $(document).on("click", "a :input,a a,a fieldset label", function(event) {
                event.stopPropagation();
            });

            $(document).off("click", '#startScreen_mobilefooter1 [name="mobilenavbaritem_7"]').on({
                click: function() {
                    if (!$(this).attr('disabled')) {
                        Apperyio.navigateTo('MapView', {
                            transition: 'flip',
                            reverse: true
                        });

                    }
                },
            }, '#startScreen_mobilefooter1 [name="mobilenavbaritem_7"]');
            $(document).off("click", '#startScreen_mobilefooter1 [name="mobilenavbaritem_8"]').on({
                click: function() {
                    if (!$(this).attr('disabled')) {
                        Apperyio.navigateTo('Dashboard', {
                            transition: 'flip',
                            reverse: true
                        });

                    }
                },
            }, '#startScreen_mobilefooter1 [name="mobilenavbaritem_8"]');

        };

    $(document).off("pagebeforeshow", "#startScreen").on("pagebeforeshow", "#startScreen", function(event, ui) {
        startScreen_beforeshow();
    });

    if (runBeforeShow) {
        startScreen_beforeshow();
    } else {
        startScreen_onLoad();
    }
};

$(document).off("pagecreate", "#startScreen").on("pagecreate", "#startScreen", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    startScreen_js();
});